import React, { useState } from 'react';
import MovieSearch from './components/MovieSearch/MovieSearch';
import MovieDetails from './components/MovieDetails/MovieDetails';
import './App.css';

import '@fontsource-variable/gabarito';

function App() {
  const [movie, setMovie] = useState(null);

  return (
    <div className="app">
      <header className="app-header">
        <img src="/LOGO1.png" alt="MovieQuest Logo" />
        <p>MovieQuest is a web application that lets users easily discover their favorite movies. 
          Leveraging the movier npm package, it provides instant access to information like titles, 
          posters, release dates, summaries, and IMDb ratings, all presented in an 
          aesthetically pleasing layout. The app includes a dynamic five-star rating 
          system and an embedded YouTube trailer feature for quick previews. </p>
      </header>
      <MovieSearch setMovie={setMovie} />
      {movie && <MovieDetails movie={movie} />}
    </div>
  );
}

export default App;
