import React, { useState } from 'react';
import { getMovie } from 'movier'; // Updated import
import './MovieSearch.scss';

const MovieSearch = ({ setMovie }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleSearch = async (e) => {
    e.preventDefault();
    try {
      const result = await getMovie(searchTerm); // Directly call the function
      const {
        name, titleYear, posterImage, plot, runtime, genres, directors, mainRate, casts
      } = result;

      setMovie({
        title: name,
        release_year: titleYear,
        poster: posterImage.url,
        overview: plot,
        runtime: runtime.value,  // Assuming 'value' has the time in minutes
        genres: genres.join(', '),
        director: directors.map((dir) => dir.name).join(', '),
        rating: mainRate.value,
        cast: casts.slice(0, 3).map((cast) => cast.name).join(', '),  // Limit to top 3
      });
      setErrorMessage('');
    } catch (error) {
      setErrorMessage('No results found.');
    }
  };

  return (
    <div className="search-container">
      <form onSubmit={handleSearch}>
        <input
          type="text"
          placeholder="Enter movie name"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button type="submit">Search</button>
      </form>
      {errorMessage && <p className="error-message">{errorMessage}</p>}
    </div>
  );
};

export default MovieSearch;
