import React from 'react';
import { motion } from 'framer-motion';
import './MovieDetails.scss';

const MovieDetails = ({ movie }) => {
  if (!movie) return null;

  const { title, poster_path, release_date, vote_average, overview } = movie;

  // Formatting release date
  const formattedDate = new Date(release_date).toLocaleDateString('en-US', {
    year: 'numeric',
    month: 'long',
    day: 'numeric',
  });

  return (
    <motion.div
      className="movie-details"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.5 }}
    >
      <img src={`http://image.tmdb.org/t/p/original${poster_path}`} alt={title} />
      <div className="movie-info">
        <h1>{title}</h1>
        <div className="info-list">
          <p><strong>Release Date:</strong> {formattedDate}</p>
          <p><strong>Rating:</strong> {vote_average}</p>
          <p><strong>Summary:</strong> <span className="summary-text">{overview}</span></p>
        </div>
      </div>
    </motion.div>
  );
};

export default MovieDetails;
